import { Link } from "react-router-dom";

import titleLogo from "./media/title-logo.png";

import "./style.min.css";

const App = () => {
  let workActive = true;
  let skillsActive = false;
  let aboutActive = false;
  let contactActive = false;

  const navClicked = (i) => {
    console.log(i);
    switch (i) {
      case "work":
        workActive = true;
        skillsActive = false;
        aboutActive = false;
        contactActive = false;
        break;
      case "skills":
        skillsActive = true;
        workActive = false;
        aboutActive = false;
        contactActive = false;
        break;
      case "about":
        aboutActive = true;
        skillsActive = false;
        workActive = false;
        contactActive = false;
        break;
      case "contact":
        contactActive = true;
        skillsActive = false;
        aboutActive = false;
        workActive = false;
        break;
      default:
    }
  };

  return (
    <div className="app">
      <header className="header">
        <div className="header-container">
          <div className="title-box">
            <img className="title-image" src={titleLogo} alt="" />
          </div>
          <nav className="navbar">
            <Link
              className={`nav-link ${workActive && "nav-link-active"}`}
              onClick={navClicked("work")}
              to="/work"
            >
              Work
            </Link>
            <Link className="nav-link" to="/skills">
              Skills
            </Link>
            <Link className="nav-link" to="/about">
              About
            </Link>
            <Link className="nav-link" to="/contact">
              Contact
            </Link>
          </nav>
        </div>
      </header>
    </div>
  );
};

export default App;
