// import dependencies
import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import PublicRoute from "../../utils/PublicRoute.js";

import { Card4 } from "../../utils/cards";

// import helpers
import { getHeight } from "../../helpers";

const Blog = () => {
  const [blog, setBlog] = useState([]);

  const getBlog = async () => {
    const allBlog = await axios.get("http://localhost:5000/blog");

    setBlog(allBlog.data);
  };

  useEffect(() => {
    getBlog();
  }, []);

  const content = (
    <div className="blog-grid" style={{ maxHeight: getHeight(65, 100) }}>
      {blog.map((i) => (
        <Card4
          createdAt={i.createdAt}
          image={i.image}
          key={i._id}
          link={i._id}
          subtitle={i.subtitle}
          text={i.text}
          title={i.title}
        />
      ))}
    </div>
  );

  return <PublicRoute content={content} />;
};

export default Blog;
